import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-view-client',
  templateUrl: './view-client.component.html',
  styleUrls: ['./view-client.component.css']
})
export class ViewClientComponent implements OnInit{
  client:any[]=[];
  message:String='';
  constructor(private http:HttpClient){}
  ngOnInit(): void {
    this.fetchClient();
  }
  fetchClient(){
    this.http.get('http://localhost:3000/getClient').subscribe((response:any)=>
    {this.client=response},
    (error)=>{console.error('Error fetching the client',error);}
    )

  }
}
<h1>View Client Meeting Schedule</h1>
<table border="5">
    <tr>
    <th>ID</th>
    <th>NAME</th>
    <th>EMAIL</th>
    <th>ADDRESS</th>
    <th>PASSWORD</th>
    <th>REPEAT_PASSWORD</th>
    <th>MEETING_TOPIC</th>
    <th>NUMBER_OF_PEOPLE</th>
    <th>START_TIME</th>
</tr>
<tr *ngFor="let clnt of client">
    <td>{{clnt.id}}</td>
    <td>{{clnt.name}}</td>
    <td>{{clnt.email}}</td>
    <td>{{clnt.address}}</td>
    <td>{{clnt.password}}</td>
    <td>{{clnt.repeatPassword}}</td>
    <td>{{clnt.meetingTopic}}</td>
    <td>{{clnt.numberOfPeople}}</td>
    <td>{{clnt.startTime}}</td>
</tr>
</table>
