import { Component, OnInit} from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-add-client',
  templateUrl: './add-client.component.html',
  styleUrls: ['./add-client.component.css']
})
export class AddClientComponent implements OnInit{
  name:String='';
  email:String='';
  address:String='';
  password:String='';
  repeatPassword:String='';
  meetingTopic:String='';
  numberOfPeople:String='';
  startTime:String='';

  constructor(private http:HttpClient){}
  ngOnInit(): void {

  }
register(){

}
}
<h1><b>Create a Client</b></h1>
<h3><b>Please fill in this form to create a client.</b></h3>
<div>
    <label> <b>Name</b></label><br>
    <input type="text" name="name" Placeholder="Enter Name" [(ngModel)]="name"/>
</div>
<div>
    <label> <b>Email</b></label><br>
    <input type="email" name="email" Placeholder="Enter Email" [(ngModel)]="email"/>  
</div>
<div>
    <label> <b>Address</b></label><br>
    <input type="text" name="address" Placeholder="Enter Address" [(ngModel)]="address"/>
</div>
<div>
    <label> <b>Password</b></label><br>
    <input type="password" name="password" Placeholder="Enter Password" [(ngModel)]="password"/>
</div>
<div>
    <label> <b>Repeat Password</b></label><br>
    <input type="password" name="repeat-password" Placeholder="Repeat password" [(ngModel)]="repeatPassword"/>
</div> 

<h1><b>Create a Meeting Schedule</b></h1>
<div>
    <label> <b>Meeting Topic</b></label><br>
    <input type="text" name="meeting-topic" Placeholder="Enter Meeting Topic" [(ngModel)]="meetingTopic"/>
</div>
<div>
    <label> <b>Number Of People</b></label><br>
    <input type="number" name="Number-Of-People" Placeholder="Enter Number Of People" [(ngModel)]="numberOfPeople"/>
</div>
<div>
    <label> <b>Start Time</b></label><br>
    <input type="datetime" name="start-Time"  [(ngModel)]="startTime"/>
</div>
<button (click)="register()">Register</button>
